// src/components/Hero.jsx
import React from 'react';
import logo from '../assets/LOGO_CMI_FINAL_PPF.png';
import '../style.css';

export default function Hero() {
  return (
    <section className="relative w-full h-[30vh] md:h-[25vh] overflow-hidden">
      {/* Fondo */}
      <div className="absolute inset-0">
        <img
          src="/assets/hero.jpg"
          alt="Fondo edificio"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black/50"></div>
      </div>

      {/* Contenido en esquina superior izquierda */}
      <div className="relative z-10 flex flex-col items-start justify-start h-full p-4">
        {/* Logo */}
        <img src={logo} alt="CMI General Solutions" className="h-24 mb-2" />

        {/* Título y subtítulo con tamaño original */}
        <h1 className="text-white text-5xl font-bold leading-tight">
          CMI General Solutions
        </h1>
        <p className="text-white text-xl">
          Soluciones integrales para el sector público y privado
        </p>
      </div>
    </section>
  );
}
